import logo from "./logo.svg";
import "./App.css";
import promptOutput from "./promptOutput.mjs";
import bias from "./components/bias.js";
import sourceInfo from "./components/sourceInfo.js";
import { useState, useEffect } from "react";

async function App() {
  const [output, setOutput] = useState({});
  useEffect(() => {
    console.log(output);
  }, [output]);
  const out = await promptOutput;
  setOutput(out);
  return (
    <div id="popup">
      <h1 id="title">WebCrack!</h1>
      <main id="main-content">
        <section className="info-section"></section>
        <h2>About the Source</h2>
        <section id="source-section"></section>
        <div className="source-info">
          <p className="info-title">Title: </p>
          <p className="info-description">{output.title}</p>
        </div>
        <div className="source-info">
          <p className="info-title">Author </p>
          <p className="info-description">{output.author}</p>
        </div>
        <div className="source-info">
          <p className="info-title">Publisher: </p>
          <p className="info-description">{output.publisher_background}</p>
        </div>
        <div className="source-info">
          <p className="info-title">Reading Length: </p>
          <p className="info-description">
            {output.article_length_minutes} min
          </p>
        </div>
        <h2>Potential Baises</h2>
        <section id="bias-section">
          <p className="bias-info">{output.bias}</p>
        </section>
        <button
          id="generate-button"
          onClick={() => {
            console.log("clicked");
          }}
        >
          Crack the Web!
        </button>
      </main>
    </div>
  );
}

export default App;
